sysauto_script.script:
// Retrieve the value of the x_snc_employee_spe.daysNotice Application Property
var daysNotice = gs.getProperty('x_snc_employee_spe.daysNotice');

// Get today's date and add the number of days from the property
var today = new GlideDateTime();
var compareDate = today.addDaysLocalTime(daysNotice);

// Get the month and day for the compareDate
var month = compareDate.getMonthLocalTime();
var day = compareDate.getDayOfMonthLocalTime();

// Query the Occasion table for special days the match the compareDate
var upcomingOccasion = new GlideRecord('x_snc_employee_spe_occasions');
upcomingOccasion.addQuery('occasion_month',month);
upcomingOccasion.addQuery('occasion_day',day);
upcomingOccasion.addQuery('private',false);
upcomingOccasion.query();

// Create an event for every record found
while(upcomingOccasion.next()){
