sysevent_in_email_action.script:
(function runAction(/*GlideRecord*/ current, /*GlideRecord*/ event, /*EmailWrapper*/ email, /*ScopedEmailLogger*/ logger, /*EmailClassifier*/ classifier) {

	// Create Incident for follow up with details from the  
	// received email
	var newInc = new GlideRecord('incident');
	newInc.short_description = "Fix incorrect birthday date";
	newInc.description = email.body.comments;
	newInc.insert();

})(current, event, email, logger, classifier);
